    this.props.eater.trucks.includes(this.props.truck) ? 
    this.setState({
        isFavorite: true
    }):
    this.setState({
        isfavorite: false
    })
    }

    render() {
        this.detectFavorite()
        console.log(this.state.isFavorite)
        return(
            <div className = "media">
                <img className= "media-image" src={this.props.truck.profile_picture}/>
                <div className= "media-body">
                <h4>{this.props.truck.name}</h4>
                {this.detectFavorite()}
