import React, { Component } from 'react';
import '../App.css';


class TruckCard extends Component {
    state = {
        favoriteTrucks: []
    }
    
    favorite = (truck) =>{
        this.setState({
            favoriteTrucks: [...favoriteTrucks, truck]
        })
        this.props.favorite(truck)
    }
    render() {
        let favoriteTrucks = this.props.eater.trucks
        console.log(favoriteTrucks)
        return(
            <div className = "media">
                <img className= "media-image" src={this.props.truck.profile_picture}/>
                <div className= "media-body">
                <h4>{this.props.truck.name}</h4>
                {favoriteTrucks.includes(this.props.truck) ?
                <button type="button" onClick={()=>console.log('unfavorite')}>Unfavorite</button> :
                <button type="button" onClick={()=>this.favorite(this.props.truck)}>Favorite</button>}
                </div>
            </div>
        )
    }
}

export default TruckCard
