import React, { Component } from 'react';
import InfoMap from '../Components/InfoMap';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    BrowserRouter,
    Redirect,
    useHistory,
    withRouter
  } from "react-router-dom";
import ReviewForm from '../Components/ReviewForm';

class TruckPage extends Component {

    state = {
        show: false
    }

    toggleReview = () =>{
        this.setState({ show: !this.state.show})
    }

    render() {
        let selectedTruckString = localStorage.getItem('selectedTruck')
        let selectedTruck = JSON.parse(selectedTruckString)
        let b = this.props.eater.reviews.filter(review => review.truck_id === selectedTruck.id)
        return(
            <div>
                <div>
                {/* <Link to='/welcome'>Back to Home Page</Link> */}
                <h1>{selectedTruck.name}</h1>
                <img className= "media-image" src={selectedTruck.profile_picture}/>
                <InfoMap truck={selectedTruck}/>
                <h2>Type of Food: {selectedTruck.type_of_food}</h2>
                <h3>Reviews</h3>
                <ul>
                    {b.map(review =>
                        <li>{review.body}
                        <br/>
                            Rating:{review.rating}</li>)}
                </ul>
                <button onClick={this.props.test}>Test</button>
                </div>

                <div>
                {this.state.show===false ? 
                <button onClick={this.toggleReview}>Write a Review</button>:
                <ReviewForm onSubmit={this.props.onSubmit} truck= {selectedTruck}/>}        
                </div>
            </div>

         ) }
}

export default TruckPage
