import logo from './logo.svg';
import './App.css';
import Map from './Components/FrontMap.js';
import MainPage from './Containers/MainPage'
import React from 'react';
import FrontPage from './Containers/FrontPage'
import TruckPage from './Containers/TruckPage'

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  BrowserRouter,
  Redirect,
  useHistory
} from "react-router-dom";


class App extends React.Component {

state = {
  trucksArray: [],
  currentEater: {},
  selectedTruck: {},
}


componentDidMount() {
  console.log('mounted')
  this.getTrucks()
}

updateFavorites = (data) => {
  let eater = this.state.currentEater
  eater.favorites = [...eater.favorites, data]
  this.setState({
    currentEater: eater
  })
}

favorite = (truck) => {
  let eater = this.state.currentEater
  eater.trucks = [...eater.trucks, truck]
  this.setState({
    currentEater: eater
  })
  this.getTrucks()
  let newFavorite = {
    eater_id: this.state.currentEater.id,
    truck_id: truck.id
  }
  let reqPackage = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json'
    },
    body: JSON.stringify(newFavorite)
  }
  fetch('http://localhost:3000/api/v1/favorites', reqPackage)
  .then(res => res.json())
  .then(data => this.updateFavorites(data))
}

unfavorite = (truck) => {
  let eater = this.state.currentEater
  let b = eater.favorites.filter(object => object.truck_id === truck.id)[0]
  let newArray = eater.favorites.filter(favorite => {
    return favorite.truck_id != truck.id;
  })
  eater.favorites = newArray
  this.setState({
    currentEater: eater
  })
  fetch(`http://localhost:3000/api/v1/favorites/${b.id}`, {
    method: "DELETE",
  })
}

setUser = (data) => {
  let obj = JSON.parse(data)
  this.setState({
    currentEater: obj,
  })
  console.log(this.state.currentEater)
}

setTrucks = (data) => {
  this.setState({
    trucksArray: data
  })
}
getTrucks = () => {
  fetch('http://localhost:3000/api/v1/trucks', {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json',
      'Authorization': `Bearer ${localStorage.token}`
    }
  })
  .then(res => res.json())
  .then(data => this.setTrucks(data))
}

test = () => {
  console.log('pleassssseee')
}

handleLogIn = (e) => {
  console.log('login hit')
  e.preventDefault()
  let eater = {
    username: e.target[0].value,
    password: e.target[1].value
  }

  let reqPackage = {
    method: 'POST',
    headers: {
      "Content-Type": "application/json",
      "Accept": "application/json",
      "Access-Control-Allow-Origin": "",
    },
    body: JSON.stringify(eater)
  }

  fetch('http://localhost:3000/api/v1/login', reqPackage)
  .then(res => res.json())
  .then(data => {
    console.log(data)
    localStorage.token = data.token
    this.setUser(data.eater)
    // this.getTrucks()
  })
}

info = (truck) => {
  console.log('clicked')
  this.setState({
    selectedTruck: truck
  })
  return <Link to='/info'/>
}

logout = () => {
  localStorage.clear()
  window.location.reload()
}

addEater = (newEater) => {
  console.log(newEater)
  let eater = {
    username: newEater.username,
    password: newEater.password
  }
  let reqPackage = {
    method: 'POST',
    header: {
      "Content-Type": "application/json",
      "Accept": "application/json",
      // 'Authorization': `Bearer ${localStorage.token}`
    },
    body: JSON.stringify(eater)
  }
  fetch('http://localhost:3000/api/v1/eaters', reqPackage)
  .then(res => res.json())
  .then(data => console.log(data))
}

reviewSubmit = (fields, truck) => {
  console.log(fields)
  let rating = parseInt(fields.rating)
  let newReview = {
    eater_id: this.state.currentEater.id,
    truck_id: truck.id,
    rating: rating,
    body: fields.body
  }
  let reqPackage = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json'
    },
    body: JSON.stringify(newReview)
  }
  fetch('http://localhost:3000/api/v1/reviews', reqPackage)
  .then(res => res.json())
  .then(data => this.updateReviews(data))
}

updateReviews = (data) => {
  let eater = this.state.currentEater
  eater.reviews = [...eater.reviews, data]
  this.setState({
    currentEater: eater
  })
  this.getTrucks()
}

// truckAverageRatings = (truck) => {
//   this.getTrucks()
//   truck = this.state.trucksArray.map(truck => truck.reviews)

// }
  render() {
  return (
    <BrowserRouter>
   {localStorage.token ? <button onClick={() =>this.logout()}>Log Out </button>: <h1>Login</h1>}
    <Switch>
      <Route exact path="/">
      {localStorage.token ? <Redirect to="/welcome"/> : <MainPage handleLogIn={this.handleLogIn} addEater={this.addEater}/>}
      </Route>
      <Route exact path='/welcome'>
      {localStorage.token ? <FrontPage trucks={this.state.trucksArray} eater={this.state.currentEater} favorite={this.favorite} unfavorite={this.unfavorite} info={this.info}/> : <Redirect to="/"/>}
      </Route>
      <Route exact path='/info'>
      {localStorage.token ? <TruckPage onSubmit={this.reviewSubmit} eater={this.state.currentEater} trucks={this.state.trucksArray}/>: <Redirect to="/"/>}
      </Route>
      {/* <Route path='/:handle' component={TruckPage}/> */}
    </Switch>

    </BrowserRouter>
  );
  }
}

export default App;
