    this.props.eater.trucks.includes(this.props.truck) ? 
    this.setState({
        isFavorite: true
    }):
    this.setState
    }

    render() {
        let favoriteTrucks = this.props.eater.trucks
        return(
            <div className = "media">
                <img className= "media-image" src={this.props.truck.profile_picture}/>
                <div className= "media-body">
                <h4>{this.props.truck.name}</h4>
                {favoriteTrucks.includes(this.props.truck) ?
                <button type="button" onClick={()=>console.log('unfavorite')}>Unfavorite</button> :
                <button type="button" onClick={()=>this.props.favorite(this.props.truck)}>Favorite</button>}
                </div>
            </div>
        )
    }
}

export default TruckCard
