    console.log(e.latLng.lat(), e.latLng.lng())
    myLatLng = { lat: e.latLng.lat(), lng: e.latLng.lng()}
    this.setState({
      markerLocation: myLatLng
    })
  }

  renderMarkers(){
    return this.props.trucks.map((truck)=> {
      return <Marker
      label = {truck.name}
      position = {truck.location}
      // icon = {truck.profile_picture}
      />
    })
  }
