import React, { Component } from 'react';
import '../App.css';

import {
    Redirect,
    withRouter
  } from "react-router-dom";


class TruckCard extends Component {

    goToInfo = (truck) => {
        localStorage.setItem('selectedTruck', JSON.stringify(truck));
        this.props.history.push('/info')
    }


link = () =>{
            return <Redirect to="/info"/>
        }

    render() {
        let b = this.props.eater.favorites.map(favorite => favorite.truck_id)
        console.log(this.props.truck)
        return(
            <div className = "media">
                <img className= "media-image" src={this.props.truck.profile_picture} onClick={()=>this.goToInfo(this.props.truck)}/>
                <div className= "media-body">
                <h2>{this.props.truck.name}</h2>
                {/* <h3>Average Rating: {this.props.truck.average_rating}</h3> */}
                <div><span class="stars-container stars-0"></span>

                </div>
                <h4>Type of Food: {this.props.truck.type_of_food}</h4>
                {b.includes(this.props.truck.id) ?
                <div class="heart-btn">
                    <div class="content">
                        <span class="heart-red" onClick={()=>this.props.unfavorite(this.props.truck)}></span>
                {/* <button type="button" onClick={()=>this.props.unfavorite(this.props.truck)}>Unfavorite</button>  */}
                </div>
                </div>:
                <div class="heart-btn">
                    <div class="content">
                        <span class= "heart" onClick={()=>this.props.favorite(this.props.truck)}></span>
                {/* <button type="button" onClick={()=>this.props.favorite(this.props.truck)}>Favorite</button> */}
                </div>
                </div>}
                </div>
            </div>
        )
    }
}

export default withRouter(TruckCard);
