import React, { Component } from 'react';

export default class CreateAccount extends Component {
    handleSubmit = (e) => {
        e.preventDefault()
        console.log(e)
        // this.props.addEater(e)
    }

    changeHandler = (e) => {
        let {name, value} = e.target
        this.setState({
            [name]: value
        })
    }

    render(){
        return (
            <div>
            <h1>Create Account</h1>
            <form onSubmit={this.handleSubmit}>
              <p>Username</p>
              <input type='text' username='username' placeholder='Enter Username' onChange={this.changeHandler} /> <br/> <br/>
              <p>Password</p>
              <input type='text' name='password' placeholder='Enter your password' onChange={this.changeHandler} /> <br/> <br/>
              <input type="submit" name="submit" value="Create Account" className="submit"/>
            </form>
        </div>
        )
    }
}
