import logo from './logo.svg';
import './App.css';
import Map from './Components/FrontMap.js';
import MainPage from './Containers/MainPage'
import React from 'react';
import FrontPage from './Containers/FrontPage'

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  BrowserRouter,
  Redirect
} from "react-router-dom";


class App extends React.Component {

state = {
  trucksArray: [],
  currentEater: {},
  currentEaterFavorites: []
}


componentDidMount() {
  console.log('mounted')
  this.getTrucks()
}

favorite = (truck) => {
  console.log(truck)
  let newFavorite = {
    eater_id: this.state.currentEater.id,
    truck_id: truck.id
  }
  let reqPackage = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json'
    },
    body: JSON.stringify(newFavorite)
  }
  fetch('http://localhost:3000/api/v1/favorites', reqPackage)
  .then(res => res.json())
  .then(data => updateFavorites(data))
}


setUser = (data) => {
  let obj = JSON.parse(data)
  this.setState({
    currentEater: obj,
    currentEaterFavorites: obj.favorites
  })
  console.log(this.state.currentEater, this.state.currentEaterFavorites)
}

setTrucks = (data) => {
  this.setState({
    trucksArray: data
  })
  console.log(this.state.trucksArray)
}
getTrucks = () => {
  fetch('http://localhost:3000/api/v1/trucks', {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json',
      'Authorization': `Bearer ${localStorage.token}`
    }
  })
  .then(res => res.json())
  .then(data => this.setTrucks(data))
}



handleLogIn = (e) => {
  console.log('login hit')
  e.preventDefault()
  let eater = {
    username: e.target[0].value,
    password: e.target[1].value
  }

  let reqPackage = {
    method: 'POST',
    headers: {
      "Content-Type": "application/json",
      "Accept": "application/json",
      "Access-Control-Allow-Origin": "",
    },
    body: JSON.stringify(eater)
  }

  fetch('http://localhost:3000/api/v1/login', reqPackage)
  .then(res => res.json())
  .then(data => {
    console.log(data)
    localStorage.token = data.token
    this.setUser(data.eater)
    // this.getTrucks()
  })
}

logout = () => {
  localStorage.clear()
  window.location.reload()
}

addEater = (newEater) => {
  console.log(newEater)
  let reqPackage = {
    method: 'POST',
    header: {
      "Content-Type": "application/json",
      "Accept": "application/json",
      'Authorization': `Bearer ${localStorage.token}`
    },
    body: JSON.stringify(newEater)
  }
  fetch('http://localhost:3000/api/v1/eaters', reqPackage)
  .then(res => res.json())
  .then(data => console.log(data))
}

  render() {
  return (
    <BrowserRouter>
   {localStorage.token ? <button onClick={() =>this.logout()}>Log Out </button>: <h1>Login</h1>}
    <Switch>
      <Route exact path="/">
      {localStorage.token ? <Redirect to="/welcome"/> : <MainPage handleLogIn={this.handleLogIn} addEater={this.addEater}/>}
      </Route>
      <Route exact path='/welcome'>
      {localStorage.token ? <FrontPage trucks={this.state.trucksArray} eater={this.state.currentEater} favorite={this.favorite}/> : <Redirect to="/"/>}
      </Route>
    </Switch>

    </BrowserRouter>
  );
  }
}

export default App;
