import logo from './logo.svg';
import './App.css';
import Map from './Components/Map.js';
import MainPage from './Containers/MainPage'
import React from 'react';

// import {
//   BrowserRouter as Router,
//   Switch,
//   Route,
//   Link,
//   BrowserRouter,
//   Redirect
// } from "react-router-dom";

class App extends React.Component {

state = {
  trucksArray: []
}


componentDidMount() {
  console.log('mounted')
  this.getTrucks()
}

getTrucks = () => {
  fetch('http://localhost:3000/api/v1/trucks', {
    method: 'GET',
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json'
    }
  })
  .then(res => res.json())
  .then(data => this.setState({
    trucksArray: data
  }))
}

handleLogIn = (e) => {
  console.log('login hit')
  e.preventDefault()
  let eater = {
    username: e.target[0].value,
    password: e.target[1].value
  }

  let reqPackage = {
    method: 'POST',
    headers: {
      "Content-Type": "application/json",
      "Accept": "application/json",
      "Access-Control-Allow-Origin": ""
    },
    body: JSON.stringify(eater)
  }

  fetch('http://localhost:3000/api/v1/login', reqPackage)
  .then(res => res.json())
  .then(data => {
    localStorage.token = data.token
  })
  console.log(localStorage)
}

addEater = (newEater) => {
  console.log(newEater)
  let reqPackage = {
    method: 'POST',
    header: {
      'Content-Type': 'application/json',
      'Accept': 'application/json'
    },
    body: JSON.stringify(newEater)
  }
  fetch('http://localhost:3000/api/v1/eaters', reqPackage)
  .then(res => res.json())
  .then(data => console.log(data))
}

  render() {
  return (
    // <BrowserRoute>
    <div className="App">
      <MainPage handleLogIn={this.handleLogIn} addEater={this.addEater}/>
        <Map />
    </div>
    // </BrowserRoute>
  );
  }
}

export default App;
