import React, { Component } from 'react';
import '../App.css';


class TruckCard extends Component {

    render() {
        let b = this.props.eater.favorites.map(favorite => favorite.truck_id)
        return(
            <div className = "media">
                <img className= "media-image" src={this.props.truck.profile_picture}/>
                <div className= "media-body">
                <h4>{this.props.truck.name}</h4>
                {console.log('rendered')}
                {b.includes(this.props.truck.id) ?
                <button type="button" onClick={()=>this.props.unfavorite(this.props.truck)}>Unfavorite</button> :
                <button type="button" onClick={()=>this.props.favorite(this.props.truck)}>Favorite</button>}
                </div>
            </div>
        )
    }
}

export default TruckCard
